import React, { useState } from 'react';
import { ethers } from 'ethers';
import QRCode from 'qrcode.react';
import ProductVerification from '../artifacts/contracts/ProductVerification.sol/ProductVerification.json';

function AdminPage() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [password, setPassword] = useState('');
  const [formData, setFormData] = useState({
    productId: '',
    name: '',
    manufacturer: '',
    productionDate: ''
  });
  const [qrCode, setQrCode] = useState('');
  const [status, setStatus] = useState('');

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    // In a real application, this should be a secure password and properly hashed
    const correctPassword = 'admin123';
    if (password === correctPassword) {
      setIsAuthenticated(true);
      setStatus('');
    } else {
      setStatus('Incorrect password');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (!window.ethereum) {
        throw new Error('Please install MetaMask to use this feature');
      }

      await window.ethereum.request({ method: 'eth_requestAccounts' });
      const provider = new ethers.BrowserProvider(window.ethereum);
      const signer = await provider.getSigner();
      
      const contract = new ethers.Contract(
        '0x5FbDB2315678afecb367f032d93F642f64180aa3',
        ProductVerification.abi,
        signer
      );

      const timestamp = Math.floor(new Date(formData.productionDate).getTime() / 1000);
      const tx = await contract.addProduct(
        formData.productId,
        formData.name,
        formData.manufacturer,
        timestamp
      );

      await tx.wait();
      setQrCode(formData.productId);
      setStatus('Product registered successfully!');
    } catch (error) {
      setStatus(`Error: ${error.message}`);
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="admin-page">
        <h2>Admin Authentication</h2>
        <form onSubmit={handlePasswordSubmit}>
          <div>
            <label>Password:</label>
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
          </div>
          <button type="submit">Login</button>
        </form>
        {status && <p className="status-message">{status}</p>}
      </div>
    );
  }

  return (
    <div className="admin-page">
      <h2>Register New Product</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Product ID:</label>
          <input
            type="text"
            name="productId"
            value={formData.productId}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Product Name:</label>
          <input
            type="text"
            name="name"
            value={formData.name}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Manufacturer:</label>
          <input
            type="text"
            name="manufacturer"
            value={formData.manufacturer}
            onChange={handleInputChange}
            required
          />
        </div>
        <div>
          <label>Production Date:</label>
          <input
            type="date"
            name="productionDate"
            value={formData.productionDate}
            onChange={handleInputChange}
            required
          />
        </div>
        <button type="submit">Register Product</button>
      </form>

      {status && <p className="status-message">{status}</p>}

      {qrCode && (
        <div className="qr-code-section">
          <h3>Generated QR Code</h3>
          <QRCode value={qrCode} size={256} />
          <p>Product ID: {qrCode}</p>
        </div>
      )}
    </div>
  );
}

export default AdminPage;
